import React, { useState } from "react";
import { alertService } from "../../Alert/alert.service";
import axios from "axios";
import "./styles.css";

const AddBlog = () => {
  const [values, setValues] = useState({
    title: "",
    category: "",
    image: "",
    intro: "",
    detail: ""
  });
  const [loader, setLoader] = useState(false);

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoader(true);
    if (!values.title || !values.content) {
      alertService.error("Please enter ALL mandatory fields!");
    } else {
      axios
        .post(
          "http://localhost:8081/miniblog/backend/v1/blogs",
          {
            ...values,
          },
          {
            headers: {
              apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
              "Access-Control-Allow-Origin": "*",
            },
          }
        )
        .then((request) => {
          setValues(request.data);
          alertService.success("Add blog successful. Thank you!");
          setLoader(false);
          console.log(request);
        })
        .catch((error) => {
          alertService.error("Something went wrong! Please try again.");
        });
    }
  };

  return (
    <div className="container">
      <form action="action_page.java">
        <div className="row">
          <div className="col-25">
            <label htmlFor="title">
              Title<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Blog title..."
              required
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="category">
              Category<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <select id="category" name="category">
              <option value="art">ART</option>
              <option value="music">MUSIC</option>
              <option value="travel">TRAVEL</option>
              <option value="technology">TECH</option>
            </select>
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="image">Image</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="image"
              name="image"
              placeholder="Image url..."
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">Introduction</label>
          </div>
          <div className="col-75">
            <textarea
              id="content"
              name="intro"
              placeholder="Write something..."
              rows="6"
              required
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">
              Detail<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <textarea
              id="detail"
              name="detail"
              placeholder="Write something..."
              rows="20"
              required
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <input type="submit" value="Submit" />
        </div>
      </form>
    </div>
  );
};

export default AddBlog;
