import React, { useEffect, useState, useMemo } from "react";
import Blog from "./index";
import axios from "axios";
import { Link } from "react-router-dom";
import style from './styles.module.scss'
import Pagination from "../Pagination/Pagination";

let PageSize = 4;

const BlogList = () => {
  const [blogs, setBlogs] = useState([]);
  const baseURL = `http://localhost:8082/miniblog/backend/v1/blogs`;
  const headers = {
    apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  };

  const [limit, setLimit] = useState(4)
  const [isLoading, setIsLoading] = useState(false);

  const fetchData = async () => {
    try {
      const url = baseURL;
      const fetch = await axios({
        method: "get",
        url: url,
        headers: {
          apikey: "691c5597-e7d2-4c06-af49-f9369b367783",
          "Access-Control-Allow-Origin": "*",
        },
        params: {
          limit: limit
        }
      }).then((request) => {
        setBlogs(request.data);
        console.log(blogs);
        console.log(request)
      });
      setIsLoading(true);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchData
    // axios.get(baseURL, { headers }).then((response) => {
    //   setBlogs(response.data);
    // });
  }, [limit]);

  const [currentPage, setCurrentPage] = useState(1);

  const currentTableData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return blogs.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);

  return (
    <div className="blog-list">
      {currentTableData.map((blog) => {
        return (
          <Blog
            key={blog.id}
            id={blog.id}
            image={blog.image}
            title={blog.title}
            intro={blog.intro}
            createdAt={blog.createdAt}
            category={blog.category}
          />
        );
      })}
      <Pagination
        className="pagination-bar"
        currentPage={currentPage}
        totalCount={blogs.length}
        pageSize={PageSize}
        onPageChange={page => setCurrentPage(page)}
      />
    </div>
  );
};

export default BlogList;
