import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import style from "./styles.module.scss";
import axios from "axios";
import { useParams } from "react-router-dom";
import Moment from "react-moment";

const BlogDetail = () => {
  const [blog, setBlog] = useState();
  const { id } = useParams();

  const baseURL = `http://localhost:8082/miniblog/backend/v1/blogs/${id}`;
  const headers = {
    apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  };

  useEffect(() => {
    axios.get(baseURL, { headers }).then((response) => {
      setBlog(response.data);
    });
  }, [id]);

  const deleteAPI = async () => {
    try {
      const mess = await APIS.household.deleteHousehold(householdId);
      alertService.success(mess.data);
      window.location.replace('/households');
    } catch (error) {
      alertService.success(error?.response?.data);
    }
  };
  const DeleteHousehold = () => {
    return (
      <>
        <div
          className={style['background']}
          onClick={() => setConfirm(false)}
        ></div>
        <div className={style['popup']}>
          <p className={style['popup_title']}>
            Bạn có chắc chắn muốn xóa không?
          </p>
          <div className={style['popup_btn']}>
            <Button
              className="btn-react"
              color="danger"
              onClick={() => setConfirm(false)}
            >
              Không
            </Button>
            <Button
              className="btn-react"
              color="success"
              onClick={() => deleteAPI()}
            >
              Xác nhận
            </Button>
          </div>
        </div>
      </>
    );
  };

  return (
    <div className={style["detail"]}>
      <img src={blog?.image} alt="image" />
      <div className={style["detail__body"]}>
        <div className={style["detail__body__header1"]}>{blog?.title}</div>
        <p className={style["detail__body__info"]}>
          <Moment date={blog?.createdAt} format="DD MMM, YYYY" /> |{" "}
          {blog?.category}
        </p>
        <hr />
        <p className={style["detail__body__last-text"]}>{blog?.detail}</p>
        <Link className={style["detail__body__touch"]} to="/">
          ← All posts
        </Link>
        <Link className={style['btn']} to={`/blogs/edit/${id}`}>edit</Link>
        <Link className={style['btn']} to="#">delete</Link>
      </div>
    </div>
  );
};

export default BlogDetail;
