import React from "react";
import { Link } from "react-router-dom";
import style from "./styles.module.scss";
import Moment from "react-moment";

const { useRef, useState, useEffect } = React;

const Blog = ({ blog }) => {
  const rootRef = useRef();
  const [isVisible, setIsVisible] = useState(false);
  const loading = 'https://tom.imgix.net/imgix_logo_curvesq.png?auto=compress&q=1&blur=100';

  useEffect(() => {
    const defaultIntersectionOptions = {
      threshold: 0,
      rootMargin: '0px',
    };

    const checkIntersections = entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      });
    };

    if (!isVisible) {
      const newIO = new IntersectionObserver(checkIntersections, defaultIntersectionOptions);
      newIO.observe(rootRef.current);
      return () => newIO.disconnect();
    }
  }, [isVisible]);
  blog = isVisible ? blog : {};
  con
	// return (
  //    <img height="600px" src={isVisible ? src : loading} ref={rootRef} sizes="(min-width: 1240px) 30vw, 90vw" />
	// );
  return (
    <div className={style["blog"]}>
      <div className={style["blog__img"]}>
        <img src={blog.image} alt="image" />
      </div>

      <div className={style["blog__body"]}>
        <Link className={style["blog__body__title"]} to={`/blogs/${blog.id}`}>
          {blog.title}
        </Link>

        <div className={style["blog__body__info"]}>
          <Moment date={blog.createdAt} format="DD MMM, YYYY" /> |{" "}
          {blog.category}
        </div>

        <span>
          {blog.intro}
          <span> </span>
        </span>

        <Link className={style["blog__body__read"]} to={`/blogs/${blog.id}`}>
          Read more...
        </Link>
      </div>
    </div>
  );
};

export default Blog;
