import React, { useEffect, useState, useMemo } from "react";
import Blog from "./index";
import axios from "axios";
import Pagination from "../Pagination/Pagination";

let PageSize = 4;

const BlogList = () => {
  const [blogs, setBlogs] = useState([]);
  const baseURL = `http://localhost:8082/miniblog/backend/v1/blogs`;
  const headers = {
    apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  };

  const [allEmployees, setAllEmployees] = useState([]);
  const [list, setList] = useState([]);

  useEffect(() => {
    axios.get(baseURL, { headers }).then((response) => {
      setBlogs(response.data);
      setList(
        allEmployees.map(t=>{
          if(list.includes(t.id)){
            return{
              select: true,
              id: t.id,
              name: t.name
            }
          }else{
            return{
              select: false,
              id: t.id,
              name: t.name
            }
          }
        })
      )
      console.log(allEmployees);// <<
      console.log(list);//  <<
    });
  }, []);

  console.log(blogs);

  const [currentPage, setCurrentPage] = useState(1);

  const currentData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return blogs.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);

  return (
    <div className="blog-list">
      {currentData.map((blog) => {
        return (
          <Blog
            key={blog.id}
            id={blog.id}
            image={blog.image}
            title={blog.title}
            intro={blog.intro}
            createdAt={blog.createdAt}
            category={blog.category}
          />
        );
      })}
      <Pagination
        className="pagination-bar"
        currentPage={currentPage}
        totalCount={blogs.length}
        pageSize={PageSize}
        onPageChange={page => setCurrentPage(page)}
      />
    </div>
  );
};

export default BlogList;
