import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { alertService } from "../../Alert/alert.service";
import axios from "axios";
import "./styles.css";

const categorySelect = [
  "ART", "MUSIC", "TRAVEL","TECH"
]

const EditBlog = () => {
  const [loader, setLoader] = useState(false);
  const [values, setValues] = useState({});
  const { id } = useParams();
  const [ blogId, setBlogId ] = useState();

  const baseURL = `http://localhost:8082/miniblog/backend/v1/blogs/${id}`;
  const headers = {
    apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  };

  useEffect(() => {
    axios.get(baseURL, { headers }).then((response) => {
      setValues(response.data);
      setBlogId(id)
    });
  }, [blogId]);

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.title]: event.target.value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoader(true);
    if (!values.title || !values.content) {
      alertService.error("Please enter ALL mandatory fields!");
    } else {
      axios
        .put(
          `http://localhost:8081/miniblog/backend/v1/blogs/${id}`,
          {
            ...values,
          },
          {
            headers: {
              apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
              "Access-Control-Allow-Origin": "*",
            },
          }
        )
        .then((request) => {
          setValues(request.data);
          alertService.success("Add blog successful. Thank you!");
          setLoader(false);
          console.log(request);
        })
        .catch((error) => {
          alertService.error("Something went wrong! Please try again.");
        });
    }
  };

  return (
    <div className="container">
      <form action="action_page.java" onSubmit={handleSubmit}>
        <div className="row">
          <div className="col-25">
            <label htmlFor="title">
              Title<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Blog title..."
              defaultValue={values?.title}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="category">
              Category<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <select id="category" name="category">
              {categorySelect.map((item) => (
                <option selected={values.category === item} value={item}>{item}</option>
              ))}
            </select>
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="image">Image</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="image"
              name="image"
              placeholder="Image url..."
              defaultValue={values?.image}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">
              INtroduction<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <textarea
              id="content"
              name="content"
              placeholder="Write something..."
              rows="6"
              defaultValue={values?.intro}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">
              Detail<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <textarea
              id="content"
              name="content"
              placeholder="Write something..."
              rows="20"
              defaultValue={values?.detail}
            />
          </div>
        </div>
        <div className="row">
          <input type="submit" value="Submit" />
        </div>
      </form>
    </div>
  );
};

export default EditBlog;
