import React, { useState } from "react";
import { alertService } from "../../Alert/alert.service";
import axios from "axios";
import "./styles.css";

const AddBlog = () => {
  const [values, setValues] = useState({
    title: "",
    category: "",
    image: "",
    intro: "",
    detail: ""
  });
  const [loader, setLoader] = useState(false);

  const handleChange = (event) => {
    setValues({
      ...values,
      [event.target.name]: event.target.value,
    });
  };

  const setCategory = (values) => {
    setValues.category = values
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoader(true);
    if (!values.title || !values.detail) {
      alert("Please enter all mandatory fields!");
    } else {
      axios
        .post(
          "http://localhost:8082/miniblog/backend/v1/blogs",
          {
            ...values,
          },
          {
            headers: {
              apikey: "2347edfd-c55c-4f59-96ee-600492f904f3",
              "Access-Control-Allow-Origin": "*",
              "Content-Type": "application/json",
            },
          }
        )
        .then((request) => {
          setValues(request.data);
          alert("Add successful. Thank you!");
          setLoader(false);
          setValues({
            title: "",
            category: "",
            image: "",
            intro: "",
            detail: ""
          })
          console.log(request);
        })
        .catch((error) => {
          console.log(values.category);//"Something went wrong! Please try again."
        });
    }
  };

  return (
    <div className="container">
      <form action="action_page.java">
        <div className="row">
          <div className="col-25">
            <label htmlFor="title">
              Title<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Blog title..."
              value={values.title}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="category">
              Category<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <select id="category" name="category" onChange={setCategory("ART")}>
              <option value={values.category}>ART</option>
              <option value={1}>MUSIC</option>
              <option value={2}>TRAVEL</option>
              <option value={3}>TECH</option>
            </select>
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="image">Image</label>
          </div>
          <div className="col-75">
            <input
              type="text"
              name="image"
              placeholder="Image url..."
              value={values.image}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">Introduction</label>
          </div>
          <div className="col-75">
            <textarea
              name="intro"
              placeholder="Write something..."
              rows="6"
              value={values.intro}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-25">
            <label htmlFor="subject">
              Detail<span>*</span>
            </label>
          </div>
          <div className="col-75">
            <textarea
              name="detail"
              placeholder="Write something..."
              rows="20"
              value={values.detail}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <input 
            type="submit" 
            name="submit" 
            value="Submit" 
            onClick={(e) => handleSubmit(e)} 
          />
        </div>
      </form>
    </div>
  );
};

export default AddBlog;
