import React from "react";
import { Link } from "react-router-dom";
import style from "./styles.module.scss";

const Blog = (blog) => {
  const convertDate = (date) => {
    // const d = new Date(date);
    var d = moment()
    return d.toDateString();
} 
  return (
    <div className={style["blog"]}>
      <img
        src={blog.image}
        alt="image"
      />
      <div className={style["blog__body"]}>
        <div className={style["blog__body__title"]}>
          {blog.title}
        </div>
        <div className={style["blog__body__info"]}>{convertDate(blog.createdAt)} | {blog.category} </div>
        <span> 
          { blog.intro }
          The rich text elements allows you to create and format headings,
          paragraph, blockquotes, image. and video all in one place onstead of
          having to add and format.
          <span> </span>
          <Link className={style["blog__body__info"]} to="/id">
            Read more...
          </Link>
        </span>
      </div>
    </div>
  );
};

export default Blog;
