import React from "react";
import { Link } from "react-router-dom";
import style from "./styles.module.scss";
import Moment from "react-moment";
let { useState, useRef, useEffect } = React;

const Blog = ({ blog, loaderIcon }) => {
  const loaderSrc = loaderIcon
      ? loaderIcon
      : "https://www.eliananunes.com/images/lazy_loader.gif",
    [imgSrc, setImgSrc] = useState(loaderSrc),
    imageRef = useRef(null),
    loader = new Image();

    useEffect(() => {
      loader.src = "https://www.eliananunes.com/images/lazy_loader.gif";   
      loader.addEventListener("load", checkImage);

      window.onscroll = window.addEventListener("scroll", lazyLoad);

      return () => {
        window.removeEventListener("scroll", lazyLoad);
      };
    }, []);

    const checkImage = () => {
      if (loader.complete) {
        lazyLoad();
      }
    };

    const lazyLoad = () => {
      if (elementInViewPort()) {
        setImgSrc(src);
      }
    };

    const elementInViewPort = () => {
      const clientRect = imageRef.current.getBoundingClientRect();

      return (
        clientRect.top >= 0 &&
        clientRect.left >= 0 &&
        clientRect.bottom - 100 <=
          (window.innerHeight || document.documentElement.clientHeight) &&
        clientRect.right <=
          (window.innerWidth || document.documentElement.clientWidth)
      );
    };

    return <img ref={imageRef} src={imgSrc} alt={alt} />;

  return (
    <div className={style["blog"]}>
      <div className={style["blog__img"]}>
        <img src={blog.image} alt="image" />
      </div>

      <div className={style["blog__body"]}>
        <Link className={style["blog__body__title"]} to={`/blogs/${blog.id}`}>
          {blog.title}
        </Link>

        <div className={style["blog__body__info"]}>
          <Moment date={blog.createdAt} format="DD MMM, YYYY" /> |{" "}
          {blog.category}
        </div>

        <span>
          {blog.intro}
          <span> </span>
        </span>

        <Link className={style["blog__body__read"]} to={`/blogs/${blog.id}`}>
          Read more...
        </Link>
      </div>
    </div>
  );
};

export default Blog;
